import React from 'react';
import './App.css';

// const App = ({title}) => (
// <div className="header">{title}</div>
// )

const testData = [
  {name: "Dan Abramov", avatar_url: "https://avatars0.githubusercontent.com/u/810438?v=4", company: "@facebook"},
  {name: "Latori Miller", avatar_url: "https://avatars0.githubusercontent.com/u/16719162?v=4", company: "Road to Hire"},
  {name: "Dylan Trimble", avatar_url: "https://avatars1.githubusercontent.com/u/42077977?v=4", company: "Road to Hire"},
];

class Form extends React.Component {
  render() {
    return (
      <form action="">
        <input type="text" placeholder="Github username" />
        <button>Add</button>
      </form>
    )
  }
}

const CardList = (props) => (
  <div>
    {testData.map(profile => <Card {...profile}/> )}
    {/* <Card {...testData[0]} />
    <Card {...testData[1]} />
    <Card {...testData[2]} /> */}
  </div>
) 


class App extends React.Component{
  render(){
    return (
    <div>
      <div className="header">{this.props.title}</div>
      {/* <ConditionalStyle /> */}
      <CardList />
    </div>
    )
  }
}


class Card extends React.Component {
  render(){
    const profile = this.props;
    return (
      <div className="github-profile">
        <img src={profile.avatar_url} />
        <div className="info">
        <div className="name">{profile.name}</div>
        <div className="company">{profile.company}</div>
        </div>
      </div>
    );
  }
}



